examples.query:
query GetCart {
  cart {
    id
    total
    itemCount
  }
}

query GetOrder($id: UUID!) {
  order(id: $id) {
    orderNumber
    status
    total
    items {
      product {
        name
      }
      quantity
    }
  }
}

query MyOrders($first: Int) {
  myOrders(first: $first) {
    edges {
      node {
        orderNumber
        createdAt
        total
      }
    }
  }
}

query GetReview($id: UUID!) {
  review(id: $id) {
    rating
    comment
    author {
      displayName
    }
  }
}

mutation CreateCustomer($input: CreateUserInput!) {
  createUser(input: $input) {
    id
    email
    displayName
    role
    profile {
      firstName
      lastName
    }
  }
}

mutation UpdateProfile($input: UserProfileInput!) {
  updateProfile(input: $input) {
    id
    profile {
      bio
      preferredLanguage
    }
  }
}

mutation AddAddress($input: AddressInput!) {
  addAddress(input: $input) {
    label
    address {
      street1
      city
      postalCode
    }
  }
}

mutation CreateProduct($input: CreateProductInput!) {
  createProduct(input: $input) {
    id
    name
    slug
    price
  }
}

mutation UpdateProduct($id: UUID!, $input: UpdateProductInput!) {
  updateProduct(id: $id, input: $input) {
    id
    price
    status
  }
}

mutation AddToCart($input: AddToCartInput!) {
  addToCart(input: $input) {
    itemCount
    total
  }
}

mutation UpdateCartItem($input: UpdateCartItemInput!) {
  updateCartItem(input: $input) {
    items {
      quantity
    }
  }
}

mutation PlaceOrder($input: PlaceOrderInput!) {
  placeOrder(input: $input) {
    ... on Order {
      id
      orderNumber
      status
      total
    }
    ... on CheckoutError {
      code
      message
    }
  }
}

mutation CreateReview($input: CreateReviewInput!) {
  createReview(input: $input) {
    id
    rating
    isApproved
  }
}

mutation MarkHelpful($id: UUID!, $helpful: Boolean!) {
  markReviewHelpful(reviewId: $id, helpful: $helpful) {
    helpfulCount
  }
}

subscription OnOrderStatusChanged($orderId: UUID!) {
  orderStatusChanged(orderId: $orderId) {
    id
    status
    updatedAt
  }
}

subscription OnNotification {
  notificationReceived {
    type
    message
    read
  }
}
